import os


def camera_first():
    print(camera_1)
    move = input("> ")

    if move.lower() == "forward":
        clear_terminal()
        camera_second()
    elif move.lower() == "back":
        print("There is nothing to go back to...")


def camera_second():
    print(camera_2)
    move = input("> ")

    if move.lower() == "forward":
        pass
    elif move.lower() == "back":
        clear_terminal()
        camera_first()
    else:
        print("Invalid command")


def clear_terminal():
    os.system('cls' if os.name == 'nt' else 'clear')


camera_first()
